        <LineSeries 
        data={this.state.rawData}
        color={'black'}
        animation
        style={{
            fill: 'none',
            strokeLinejoin: 'round',
            strokeWidth: 8
        }}
        curve={'curveMonotoneX'}
        />

        {/* <MarkSeries
          onValueMouseOver={this._rememberValue}
          onValueMouseOut={this._forgetValue}
          data={this.state.rawData}
          id="markSeries"
        /> */}
        {/* {value ? <Hint value={value} /> : null} */}

        <LabelSeries
        data={this.state.rawData}
        // labelAnchorX={"end"}
        // labelAnchorY={"middle"}
        />
        <LabelSeries
        data={this.state.labelData}
        labelAnchorX={"start"}
        labelAnchorY={"middle"}
        />

        <LabelSeries
        data={this.state.dayLabels}
        labelAnchorX={"end"}
        labelAnchorY={"middle"}
        />



        {/* <HorizontalBarSeries 
        data={this.state.rawData}
        color={'url(#CoolGradient)'}
        animation
        /> */}

         {/* <YAxis 
