import React, { Component } from 'react';
// import './App.css';
// import '../node_modules/react-vis/dist/style.css';
import {XYPlot, LineSeries, HorizontalGridLines, XAxis, YAxis, VerticalGridLines, VerticalBarSeries, GradientDefs, AreaSeries, FlexibleXYPlot} from 'react-vis';

class SummaryChart extends Component {
  render() {
    const data = [
      {x: 0, y: 8},
      {x: 1, y: 5},
      {x: 2, y: 4},
      {x: 3, y: 9},
      {x: 4, y: 1},
      {x: 5, y: 7},
      {x: 6, y: 6},
      {x: 7, y: 3},
      {x: 8, y: 2},
      {x: 9, y: 0}
    ];
    return (
      <div>
<div
      style={{
        display: 'flex',
        justifyContent: 'space-between',
        position: 'relative',
        width:  '100%',
        height: '30vh'
      }}
    >
<div
        className="flexible-vis"
        style={{width: '99%', height: '100%', border: '1px solid #ccc'}}
      >
        <FlexibleXYPlot>
          <VerticalBarSeries data={data} />
        </FlexibleXYPlot>
      </div>

</div>

        {/* <XYPlot height={300} width={300} color="red"> */}

        {/* </XYPlot> */}

        </div>
    );
  }
}

export default SummaryChart;
