export class Hero extends Component {
    render() {
        return (
            <div>

             <div height={5} id="header">

<div id="header-content" style={{paddingLeft:'3vw'}}>
<Segment.Group compact horizontal style={{background: "none"}}>
    <Segment style={{padding: "0px !important"}}>
        <img alt="Carbon Forecast Cloud Logo" className="img-header" src={this.props.mobileUser ? LogoSmall : Logo} />
    </Segment>
    <Segment compact style={{lineHeight: "11vw "}}>
        {/* <span id="header-text" style={this.props.mobileUser ? {fontSize: "8vh"} : {fontSize: "80px"}}>Carbon Forecast</span> */}
        <div style={{lineHeight: "200px !important"}}>
        <div id="header-text" style={this.props.mobileUser ? {fontSize: "8vh"} : {fontSize: "80px"}}>Carbon Forecast</div>
        </div>    
    </Segment>
</Segment.Group>

