export class Timeline extends Component {

    state = {
        rawData: [{y: 'C', x: 0},
            {y: 'A', x: 10},
            {y: 'B', x: 20},
            {y: 'C', x: 30},
            {y: 'D', x: 40},
            {y: 'E', x: 50},
            {y: 'F', x: 60},
            {y: 'G', x: 70},
            {y: 'H', x: 80},
            {y: 'I', x: 90}],
        barPosition: {date: '', offset: 0}
    }


    render() {
        return (
            <div>


                <div id="timeline">
<div
      style={{
       // display: 'flex',
        justifyContent: 'space-between',
        position: 'relative',
        width:  '100%',
        //height: '50vh',
        height: '150vh',
        overflow: 'auto',
      }}

    >
<div
        style={{width: '100%', height: '100%', border: '1px solid #ccc'}}
      >
        <FlexibleXYPlot  yType="ordinal" margin={{bottom: 80, left: 0, right: 0, top: 20}}>

        <GradientDefs>
          <linearGradient id="CoolGradient" y1="0" y2="0" x1="0" x2="1">
            <stop offset="0%" stopColor="green" stopOpacity={0.4}/>
            <stop offset="50%" stopColor="yellow" stopOpacity={0.4}/>
            <stop offset="100%" stopColor="red" stopOpacity={0.4} />
          </linearGradient>
        </GradientDefs>

        <VerticalGridLines />
        <HorizontalGridLines />

        <YAxis 
        position="start"
        // top={40}
        top={30}
        //height={150}
        //width={1}
        style={{
            text: {stroke: 'none', fill: '#6b6b76', fontWeight: 800, fontSize: 10}
          }}
          //tickLabelAngle={-45}
          tickTotal={1}
          />

        {/* <YAxis /> */}

        <HorizontalBarSeries 
        data={this.state.rawData}
        color={'url(#CoolGradient)'}
        animation
        />
        <LabelSeries
                    data={this.state.rawData.map(obj => {
                        return { ...obj, label: obj.y.toString() }
                    })}

                    animation
                    labelAnchorX="middle"
                    labelAnchorY="text-before-edge"

                    />


{/* 
        <LabelSeries

                    //data={this.xlabelData('top')}
