import React, { Component } from 'react';
import logo from './logo.svg';
import {
  BrowserRouter as Router,
  Switch,
  Route
} from "react-router-dom";
import './App.css';
import API from './adapters/API'
import Nav from './components/Nav';
import Start from './components/Start';
import Forecast from './components/Forecast';
import About from './components/About';
import Advice from './components/Advice';


export class App extends Component {


  state = {
    setRegion: 18,
    setPostCode: '',
    useId: true,
    regionIndex: [{id: 0, name: 'test'}],
    forecastA: {},
    forecastB: {},
    forecastC: {}
  }


//2017-08-25T12:35Z


now = () => {
  let date = new Date();
  return date.toISOString()
}

  componentDidMount () {
    this.get48hForecast(this.state.setRegion, this.now())
    this.compileRegionIndex()
  }

  componentDidUpdate (prevProps, prevState) {
    if (this.state.forecastA !== prevState.forecastA)
    {
      return this.getForecastB(this.state.setRegion)
    }
    else if (this.state.forecastB !== prevState.forecastB)
    {
      return this.getForecastC(this.state.setRegion)
    }
  }

get48hForecast = (start) => {
  API.getRegionId48HrsData(id, start).then(info => this.setState({forecastA: info.data.data}))
}
getForecastB = () => {
  const start = this.state.forecastA[this.state.forecastA.length - 1].to
  if (this.state.useId) {
  API.getRegionId48HrsData(this.state.setRegion, start).then(info => this.setState({forecastB: info.data.data}))
}else{
  API.getRegionPostCode48HrsData(this.state.setPostCode, start).then(info => this.setState({forecastB: info.data.data}))
  } 
}


getForecastB = () => {
  const start = this.state.forecastA[this.state.forecastA.length - 1].to
  if (this.state.useId) {
  API.getRegionId48HrsData(this.state.setRegion, start).then(info => this.setState({forecastB: info.data.data}))
}else{
  API.getRegionPostCode48HrsData(this.state.setPostCode, start).then(info => this.setState({forecastB: info.data.data}))
  } 
}





  compileRegionIndex = () => {
    API.getCurrentRegionalData().then(info => info.data[0].regions.map(item => {
          this.setState({
              regionIndex: 
                [...this.state.regionIndex,
                {id: item.regionid,
                name: item.shortname}]
                }
          )
       }
      )
    )
}


  render() {
    return (


<Router>


<div>

<Nav />

<Switch>


    <Route exact path="/">
          <Start />
    </Route>
    <Route path="/start">
          <Start />
    </Route>
    <Route path="/forecast">
          <Forecast />
    </Route>
    <Route path="/advice">
          <Advice />
    </Route>
    <Route path="/about">
          <About />
    </Route>

</Switch>

</div>

</Router>

    );
  }
}

export default App;
