const apiEndpoint = `https://api.carbonintensity.org.uk/`
const nationalURL = `${apiEndpoint}/intensity`
const allRegionsURL = `${apiEndpoint}/regional`



const getData = (URL) => fetch(URL).then(response => response.json())

const compileRegionIndex = () => {

    const regionIndex = [
        {key: 0, name: 'test'}
    ]

    getData(allRegionsURL).then(info => info.data[0].regions.map(item => {
            regionIndex = [
                ...regionIndex,
                {key: item.regionid,
                name: item.shortname}
            ]
    }
    )
    )
    return 
}



export default {
    getData,
    compileRegionIndex,
}
