import _ from 'lodash'
import PostcodeJsLkp from '../content/PostcodeJsLkp';
import React, { Component } from 'react'
import { Search, Button, Grid, Header, Segment } from 'semantic-ui-react'

const initialState = { isLoading: false, results: [{title:''}], value: '', confirmResult: ''}


const source = (
    PostcodeJsLkp.postcodes
)



export default class PostCodeSearch extends Component {
  state = initialState

  handleResultSelect = (e, { result }) => {this.setState({ value: result.title }), this.confrimResult(result.title)}

  handleSearchChange = (e, { value }) => {
    this.setState({ isLoading: true, value })

    setTimeout(() => {
      if (this.state.value.length < 2) return this.setState(initialState)

      const re = new RegExp(_.escapeRegExp(this.state.value), 'i')
      const isMatch = (result) => re.test(result.title)

      this.setState({
        isLoading: false,
        results: _.filter(source, isMatch),
      })

    }, 300)

  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.value !== this.state.value) {
      this.props.updatePostCode(this.state.value)
    }
  }


  confirmResult = (value) => {

    console.log('confirming')
    let result = this.source.filter(postcode => value === postcode)[0]
    if (typeof result !== 'undefined'){
      this.setState({confirmResult: result})
    }

  }

  postCodeSubmit = () => {
    if (this.state.confirmResult !== ''){
      this.props.updatePostCode(this.state.confirmResult)
    }

  }


  render() {
    const { isLoading, value, results } = this.state

    return (
        <div>
          <Search
            loading={isLoading}
            onResultSelect={this.handleResultSelect}
            onSearchChange={_.debounce(this.handleSearchChange, 500, {
              leading: true,
            })}
            results={results}
            value={value}
            {...this.props}
          />

          <Button onClick={event => postCodeSubmit()}> >Submit</Button>
          </div>
    )
  }
}
