import React, { Component } from 'react';
// import './App.css';
// import '../node_modules/react-vis/dist/style.css';
import {XYPlot, LineSeries, HorizontalGridLines, XAxis, YAxis, VerticalGridLines, VerticalBarSeries, GradientDefs, AreaSeries, makeWidthFlexible} from 'react-vis';





class SummaryChart extends Component {
  render() {
    const data = [
      {x: 0, y: 8},
      {x: 1, y: 5},
      {x: 2, y: 4},
      {x: 3, y: 9},
      {x: 4, y: 1},
      {x: 5, y: 7},
      {x: 6, y: 6},
      {x: 7, y: 3},
      {x: 8, y: 2},
      {x: 9, y: 0}
    ];
    return (
      <div>


        <XYPlot height={300} width={width} color="red">

        <GradientDefs>
          <linearGradient id="CoolGradient" x1="0" x2="0" y1="0" y2="1">
            <stop offset="0%" stopColor="red" stopOpacity={0.4}/>
            <stop offset="100%" stopColor="blue" stopOpacity={0.3} />
          </linearGradient>
        </GradientDefs>

        <VerticalGridLines />
        <HorizontalGridLines />
        <XAxis />
        <YAxis />
        <FlexibleWidthXYPlot height={100}>
        <VerticalBarSeries data={data} color={'url(#CoolGradient)'}/>
        </FlexibleWidthXYPlot>
        </XYPlot>
        </div>
    );
  }
}

export default SummaryChart;
