import React, { Component } from 'react';
// import './App.css';
// import '../node_modules/react-vis/dist/style.css';
import {XYPlot, LineSeries, HorizontalGridLines, XAxis, YAxis, VerticalGridLines, VerticalBarSeries, GradientDefs, AreaSeries, FlexibleXYPlot, LabelSeries} from 'react-vis';

class SummaryChart extends Component {
  render() {
    const data = [
      {x: 0, y: 8},
      {x: 1, y: 5},
      {x: 2, y: 4},
      {x: 3, y: 9},
      {x: 4, y: 1},
      {x: 5, y: 7},
      {x: 6, y: 6},
      {x: 7, y: 3},
      {x: 8, y: 2},
      {x: 9, y: 0}
    ];
    return (
      <div>
<div
      style={{
        display: 'flex',
        justifyContent: 'space-between',
        position: 'relative',
        width:  '100%',
        height: '30vh'
      }}
    >
<div
        style={{width: '99%', height: '100%', border: '1px solid #ccc'}}
      >
        <FlexibleXYPlot>
        <GradientDefs>
          <linearGradient id="CoolGradient" x1="0" x2="0" y1="0" y2="1">
            <stop offset="0%" stopColor="red" stopOpacity={0.4}/>
            <stop offset="100%" stopColor="blue" stopOpacity={0.3} />
          </linearGradient>
        </GradientDefs>

        <VerticalGridLines />
        <HorizontalGridLines />
        <XAxis />
        <YAxis />
        <VerticalBarSeries 
        data={data}
        color={'url(#CoolGradient)'}
        animation
        />
        <LabelSeries
                    data={data.map(obj => {
                        return { ...obj, label: obj.y.toString() }
                    })}
                    animation
                    labelAnchorX="middle"
                    labelAnchorY="text-before-edge"
                    />
        </FlexibleXYPlot>
      </div>

</div>


        </div>
    );
  }
}

export default SummaryChart;
